int ok[1331];
int path[1331];
stack <char> res;
int ways[6] = {-1, 1, 121, 11, -121, -11};
char getChar(int diff){
	switch(diff)
	{
		case -121:
			return 'b';
		case 11:
			return 'd';
		case 121:
			return 'f';
		case -1:
			return 'l';
		case 1:
			return 'r';
		case -11:
			return 'u';
	}
}

void getPath(int u){
	while(maze[u] != 2){
		res.push(getChar(u - path[u]));
		u = path[u];
	}
	while(!res.empty()){
		//char tmp = res.top();
		cout<<res.top();
		res.pop();
	}
	cout<<endl;
	return;
}

void DFS(int u, int cnt){
	if(maze[u] == 3){
		getPath(u);
		return;
	}
	if(cnt > 28) return;
	ok[u] = true;
	for(int i = 0; i < 6; i++){
		int v = u + ways[i];
		//cout<<v;
		if (v >= 1331 || v < 0 || ok[v] == true)
			continue;
		if(maze[v] != 0){
			path[v] = u;
			DFS(v, cnt + 1);
			ok[v] = false;
		}

	}
}

int main(){
	memset(ok, 0, sizeof(ok));
	DFS(84, 0);
	return 0;
}
